// Data inspection - return structure as output for debugging
const inputData = $input.item;
const jsonData = inputData ? inputData.json : null;

// Return debug information as workflow output
if (!jsonData) {
  return [{
    json: {
      error: 'No JSON data received',
      inputData: inputData,
      debugInfo: 'HTTP Request returned no data'
    }
  }];
}

// Check if content exists and is array
if (jsonData.content && Array.isArray(jsonData.content)) {
  // Success! Extract the content
  return [{
    json: {
      success: true,
      scriptLines: jsonData.content,
      totalScriptLines: jsonData.content.length,
      debugInfo: `Successfully found content array with ${jsonData.content.length} items`
    }
  }];
}

// Return structure for debugging
return [{
  json: {
    error: 'JSON does not have expected content array',
    jsonDataType: typeof jsonData,
    jsonDataKeys: Object.keys(jsonData || {}),
    hasContent: 'content' in (jsonData || {}),
    contentType: jsonData && jsonData.content ? typeof jsonData.content : 'undefined',
    contentIsArray: jsonData && jsonData.content ? Array.isArray(jsonData.content) : false,
    fullStructure: jsonData,
    debugInfo: 'Check the fullStructure property to see what was actually received'
  }
}];

// Debug and create script items
try {
  console.log('🎬 Starting Create Script Items...');

  const config = $('Set Configuration').item.json;
  const contentData = $input.item.json;

  console.log('📋 Config exists:', !!config);
  console.log('📋 Config keys:', config ? Object.keys(config) : 'none');
  console.log('📋 ContentData exists:', !!contentData);
  console.log('📋 ContentData keys:', contentData ? Object.keys(contentData) : 'none');

  // Check if contentData has scriptLines or if it's the extracted content itself
  let scriptLines = [];

  if (contentData.scriptLines) {
    scriptLines = contentData.scriptLines;
    console.log('📚 Found scriptLines property with', scriptLines.length, 'items');
  } else if (contentData.success && contentData.scriptLines) {
    scriptLines = contentData.scriptLines; 
    console.log('📚 Found success with scriptLines:', scriptLines.length, 'items');
  } else if (Array.isArray(contentData)) {
    scriptLines = contentData;
    console.log('📚 ContentData is array with', scriptLines.length, 'items');
  } else {
    console.error('❌ No scriptLines found in contentData');
    console.error('ContentData structure:', JSON.stringify(contentData, null, 2));
    return [{
      json: {
        error: 'No scriptLines found',
        contentDataKeys: Object.keys(contentData || {}),
        contentDataStructure: contentData
      }
    }];
  }

  const maxItems = config.maxItems || 5;
  const startIndex = config.startIndex || 0;

  console.log('📚 Total script lines:', scriptLines.length);
  console.log('🔢 Max items per batch:', maxItems);
  console.log('🎯 Starting from index:', startIndex);

  // Apply batching
  const endIndex = Math.min(startIndex + maxItems, scriptLines.length);
  const selectedLines = scriptLines.slice(startIndex, endIndex);
  const totalBatches = Math.ceil(scriptLines.length / maxItems);
  const currentBatch = Math.floor(startIndex / maxItems) + 1;

  console.log(`📊 Processing batch ${currentBatch}/${totalBatches}: lines ${startIndex + 1} to ${endIndex}`);
  console.log('📦 Selected lines:', selectedLines.length);

  if (selectedLines.length === 0) {
    console.warn('⚠️ No lines selected! All processing complete.');
    return [];
  }

  // Create individual items for processing
  const items = selectedLines.map((line, index) => ({
    json: {
      ...line,
      voiceId: line.speaker === 'Victor' ? config.victorVoiceId : config.lennyVoiceId,
      cloudinaryFolder: config.cloudinaryFolder,
      cloudName: config.cloudName,
      itemIndex: index + 1,
      totalItems: selectedLines.length,
      batchStartIndex: startIndex,
      globalIndex: startIndex + index + 1,
      totalScriptLines: scriptLines.length,
      currentBatch: currentBatch,
      totalBatches: totalBatches,
      publicId: `${config.cloudinaryFolder}/${line.fileName.replace('.wav', '')}`,
      // Loop control data
      nextStartIndex: endIndex,
      hasMoreBatches: endIndex < scriptLines.length,
      originalConfig: {
        victorVoiceId: config.victorVoiceId,
        lennyVoiceId: config.lennyVoiceId,
        cloudinaryFolder: config.cloudinaryFolder,
        cloudName: config.cloudName,
        maxItems: config.maxItems,
        contentUrl: config.contentUrl,
        scriptLines: scriptLines
      }
    }
  }));

  console.log('📦 Created', items.length, 'items for processing');
  return items;

} catch (error) {
  console.error('❌ Error in Create Script Items:', error.message);
  return [{
    json: {
      error: error.message,
      debugInfo: 'Check console logs for details'
    }
  }];
}

const current = $input.item.json;
console.log(`🎤 Processing ${current.globalIndex}/${current.totalScriptLines}: ${current.fileName} (${current.speaker})`);
console.log(`📦 Batch progress: ${current.itemIndex}/${current.totalItems}`);
console.log(`🔍 DEBUG: Text content:`, current.text ? `"${current.text.substring(0, 50)}..."` : 'NULL/UNDEFINED');
console.log(`🔍 DEBUG: VoiceId:`, current.voiceId);
console.log(`🔍 DEBUG: All keys:`, Object.keys(current));
return $input.all();

const current = $input.item.json;
console.log(`🔊 Audio generated successfully for: ${current.fileName}`);
return $input.all();

const current = $input.item.json;
console.log(`✅ Successfully uploaded: ${current.public_id}`);
console.log(`🔗 URL: ${current.secure_url}`);
return $input.all();

// Check for more batches
const allItems = $input.all();
const totalItems = allItems.length;

console.log(`🎉 BATCH COMPLETED! Processed ${totalItems} files successfully`);

if (totalItems === 0) {
  console.error('❌ ERROR: No items received');
  return [];
}

const firstItem = allItems[0];
const nextStartIndex = firstItem.json.nextStartIndex;
const hasMoreBatches = firstItem.json.hasMoreBatches;
const currentBatch = firstItem.json.currentBatch;
const totalBatches = firstItem.json.totalBatches;
const originalConfig = firstItem.json.originalConfig;

console.log(`📊 Completed batch ${currentBatch}/${totalBatches}`);
console.log(`📊 HasMoreBatches: ${hasMoreBatches}`);

if (hasMoreBatches) {
  console.log(`🔄 Will continue to next batch from index ${nextStartIndex}`);
} else {
  console.log('🎊 ALL BATCHES COMPLETED!');
}

return [{
  json: {
    hasMoreBatches: hasMoreBatches,
    nextStartIndex: nextStartIndex,
    currentBatch: currentBatch,
    totalBatches: totalBatches,
    originalConfig: originalConfig
  }
}];

// Setup next batch
const inputItem = $input.item.json;
const config = inputItem.originalConfig;
const nextStartIndex = inputItem.nextStartIndex;

console.log(`🔄 Setting up next batch from index ${nextStartIndex}`);

return [{
  json: {
    ...config,
    startIndex: nextStartIndex
  }
}];

const current = $input.item.json;
console.error(`❌ Audio generation failed for: ${current.fileName}`);
return $input.all();

const current = $input.item.json;
console.error(`❌ Upload failed for: ${current.public_id || current.fileName}`);
return $input.all();
