// Collect all error information
const errors = {
  preset1: null,
  preset2: null, 
  preset3: null,
  signed: null
};

// Get errors from each test
const allItems = $input.all();
allItems.forEach((item, index) => {
  if (item.json.error) {
    const errorMsg = item.json.error.message || item.json.error;
    const statusCode = item.json.error.httpCode || 'unknown';

    console.log(`Test ${index + 1} Error:`, statusCode, '-', errorMsg);

    if (index === 0) errors.preset1 = { status: statusCode, message: errorMsg };
    else if (index === 1) errors.preset2 = { status: statusCode, message: errorMsg };
    else if (index === 2) errors.preset3 = { status: statusCode, message: errorMsg };
    else if (index === 3) errors.signed = { status: statusCode, message: errorMsg };
  }
});

console.log('');
console.log('üîç COMPREHENSIVE DIAGNOSIS:');

// Check for common patterns
const hasPresetError = Object.values(errors).some(e => 
  e && e.message && e.message.includes('Upload preset must be specified')
);

const hasCloudNameError = Object.values(errors).some(e => 
  e && e.message && e.message.includes('cloud_name is disabled')
);

const hasAuthError = Object.values(errors).some(e => 
  e && (e.status === 401 || e.status === 403)
);

if (hasCloudNameError) {
  console.log('üö® PRIMARY ISSUE: Account disabled ("cloud_name is disabled")');
