// Prepare parameters for signature calculation
const timestamp = Math.round(Date.now() / 1000);
const fileName = `test_audio_${new Date().toISOString().replace(/[:.]/g, '-').slice(0, 19)}`;
const publicId = `${$input.item.json.cloudinaryFolder}/${fileName}`;

console.log('üéµ Audio generated successfully, preparing for signature calculation');
console.log('üìÑ File name:', fileName + '.wav');
console.log('üÜî Cloudinary public ID:', publicId);
console.log('‚è∞ Timestamp:', timestamp);

// Prepare string for signing
const apiSecret = $input.item.json.apiSecret;
const stringToSign = `public_id=${publicId}&timestamp=${timestamp}` + apiSecret;

console.log('üìù String for signing prepared');
console.log('üîÑ Next: Will calculate SHA1 using external service');

// Convert binary data to base64
const binaryData = $input.item.binary.data;
const base64Data = binaryData.data;

return [{
  json: {
    ...($input.item.json || {}),
    fileName: fileName + '.wav',
    publicId: publicId,
    timestamp: timestamp,
    stringToSign: stringToSign,
    fileData: `data:audio/wav;base64,${base64Data}`
  }
}];

// Extract signature from external SHA1 service response
const sha1Response = $input.item.json;
const signature = sha1Response.sha1 || sha1Response.hash || sha1Response.result;

if (!signature) {
  console.error('‚ùå Could not extract SHA1 signature from response:', sha1Response);
  throw new Error('SHA1 calculation failed - no signature in response');
}

console.log('üîè SHA1 Signature calculated:', signature);
console.log('‚úÖ Ready for Cloudinary signed upload');

// Combine with original data
const originalData = $input.first().json;

return [{
  json: {
    ...originalData,
    signature: signature
  }
}];

const uploadResult = $input.item.json;
const publicId = uploadResult.public_id;
const secureUrl = uploadResult.secure_url;
const originalFileName = uploadResult.original_filename || 'test_audio';
const format = uploadResult.format;
const bytes = uploadResult.bytes;
const duration = uploadResult.duration;
const signature = uploadResult.signature;

console.log('üéâ SUCCESS! Audio file uploaded to Cloudinary using SIGNED authentication!');
console.log('üìÑ Original file name:', originalFileName + '.' + format);
console.log('üÜî Cloudinary public ID:', publicId);
console.log('üîó Secure URL:', secureUrl);
console.log('üìä File size:', Math.round(bytes / 1024) + ' KB');
if (duration) {
  console.log('‚è±Ô∏è Duration:', Math.round(duration) + ' seconds');
}
console.log('üîè Upload signature:', signature);
console.log('‚úÖ ElevenLabs + Cloudinary signed integration working perfectly!');
console.log('üåê Using external SHA1 service for signature calculation');

// Generate format URLs
const mp3Url = secureUrl.replace(/\.(\w+)$/, '.mp3').replace('/upload/', '/upload/f_mp3/');
console.log('üîÑ Available formats:');
console.log(`   - wav: ${secureUrl}`);
console.log(`   - mp3: ${mp3Url}`);

console.log('\nüéØ INTEGRATION SUCCESS SUMMARY:');
console.log('‚úÖ ElevenLabs API: Working');
console.log('‚úÖ Cloudinary Signed Upload: Working');
console.log('‚úÖ External SHA1 Service: Working');
console.log('‚úÖ File Processing: Complete');
console.log('‚úÖ CDN URLs: Generated');

return [{ 
  json: { 
    success: true, 
    message: 'Test completed successfully with Cloudinary signed upload using external SHA1',
    fileName: originalFileName + '.' + format,
    cloudinaryPublicId: publicId,
    secureUrl: secureUrl,
    fileSize: Math.round(bytes / 1024) + ' KB',
    duration: duration ? Math.round(duration) + 's' : 'N/A',
    authMethod: 'Cloudinary Signed Authentication (External SHA1)',
    uploadSignature: signature,
    timestamp: new Date().toISOString()
  }
}];

