// Process remote JSON content
const config = $input.first().json;
const remoteData = $input.last().json;

console.log('ðŸŒ Processing remote JSON content...');
console.log('ðŸ“Š Content source:', config.contentSourceType);
console.log('ðŸ”— Content URL:', config.contentUrl);

let scriptLines = [];

// Handle different JSON structures
if (Array.isArray(remoteData)) {
  scriptLines = remoteData;
  console.log('ðŸ“„ Found direct array with', scriptLines.length, 'items');
} else if (remoteData.content && Array.isArray(remoteData.content)) {
  scriptLines = remoteData.content;
  console.log('ðŸ“„ Found content array with', scriptLines.length, 'items');
} else if (remoteData.data && Array.isArray(remoteData.data)) {
  scriptLines = remoteData.data;
  console.log('ðŸ“„ Found data array with', scriptLines.length, 'items');
} else {
  throw new Error('JSON must contain an array or have content/data property with an array');
}

// Validate required fields
const missingFields = [];
scriptLines.forEach((item, index) => {
  if (!item.text) missingFields.push(`Item ${index + 1}: missing 'text'`);
  if (!item.fileName) missingFields.push(`Item ${index + 1}: missing 'fileName'`);
  if (!item.speaker) missingFields.push(`Item ${index + 1}: missing 'speaker'`);
});

if (missingFields.length > 0) {
  console.error('âŒ Content validation errors:');
  missingFields.forEach(error => console.error(`   ${error}`));
  throw new Error('Content validation failed');
}

console.log('âœ… Content validation passed');

// Return config with processed content
return [{
  json: {
    ...config,
    scriptLines: scriptLines,
    totalScriptLines: scriptLines.length
  }
}];

