// Create batched script items
const config = $input.item.json;
const scriptLines = config.scriptLines || [];
const maxItems = config.maxItems || 5;
const startIndex = config.startIndex || 0;

console.log('ðŸŽ¬ Processing script lines...');
console.log('ðŸ“š Total script lines:', scriptLines.length);
console.log('ðŸ”¢ Max items per batch:', maxItems);
console.log('ðŸŽ¯ Starting from index:', startIndex);

// Apply batching
const endIndex = Math.min(startIndex + maxItems, scriptLines.length);
const selectedLines = scriptLines.slice(startIndex, endIndex);
const totalBatches = Math.ceil(scriptLines.length / maxItems);
const currentBatch = Math.floor(startIndex / maxItems) + 1;

console.log(`ðŸ“Š Processing batch ${currentBatch}/${totalBatches}: lines ${startIndex + 1} to ${endIndex}`);

if (selectedLines.length === 0) {
  console.warn('âš ï¸ No lines selected! All processing complete.');
  return [];
}

// Create individual items for processing
const items = selectedLines.map((line, index) => ({
  json: {
    ...line,
    voiceId: line.speaker === 'Victor' ? config.victorVoiceId : config.lennyVoiceId,
    cloudinaryFolder: config.cloudinaryFolder,
    cloudName: config.cloudName,
    itemIndex: index + 1,
    totalItems: selectedLines.length,
    batchStartIndex: startIndex,
    globalIndex: startIndex + index + 1,
    totalScriptLines: scriptLines.length,
    currentBatch: currentBatch,
    totalBatches: totalBatches,
    publicId: `${config.cloudinaryFolder}/${line.fileName.replace('.wav', '')}`,
    // Loop control data
    nextStartIndex: endIndex,
    hasMoreBatches: endIndex < scriptLines.length,
    originalConfig: {
      victorVoiceId: config.victorVoiceId,
      lennyVoiceId: config.lennyVoiceId,
      cloudinaryFolder: config.cloudinaryFolder,
      cloudName: config.cloudName,
      maxItems: config.maxItems,
      contentSourceType: config.contentSourceType,
      contentUrl: config.contentUrl,
      scriptLines: scriptLines
    }
  }
}));

console.log('ðŸ“¦ Created', items.length, 'items for processing');
return items;

