// Get all successful upload items and use their original data
const allItems = $input.all();
const totalItems = allItems.length;

console.log('');
console.log(`🎉 BATCH COMPLETED! Processed ${totalItems} files successfully`);

if (totalItems === 0) {
  console.error('❌ ERROR: No items received at all!');
  return [];
}

console.log('🔍 DEBUGGING DATA LOSS: Analyzing all items...');
allItems.forEach((item, index) => {
  console.log(`📦 Item ${index + 1}:`);
  console.log(`   Has json: ${!!item.json}`);
  console.log(`   Has originalConfig: ${!!item.json?.originalConfig}`);
  if (item.json?.originalConfig) {
    console.log(`   originalConfig keys: ${Object.keys(item.json.originalConfig)}`);
    console.log(`   has contentUrl: ${!!item.json.originalConfig.contentUrl}`);
    if (item.json.originalConfig.contentUrl) {
      console.log(`   contentUrl value: ${item.json.originalConfig.contentUrl}`);
    }
  }
});

// Get batch info from first item - this contains the correct nextStartIndex
const firstItem = allItems[0];
if (!firstItem?.json) {
  console.error('❌ ERROR: No valid items found');
  return [];
}

// Use the EXACT values calculated by Process Remote Content
const nextStartIndex = firstItem.json.nextStartIndex;
const totalContentItems = firstItem.json.totalContentItems;
const hasMoreBatches = firstItem.json.hasMoreBatches;
const currentBatch = firstItem.json.currentBatch;
const totalBatches = firstItem.json.totalBatches;
const originalConfig = firstItem.json.originalConfig;

console.log(`📊 BATCH COMPLETION: Completed batch ${currentBatch}/${totalBatches}`);
console.log(`📊 NextStartIndex: ${nextStartIndex}`);
console.log(`📊 HasMoreBatches: ${hasMoreBatches}`);
console.log('🔍 CRITICAL DEBUG: originalConfig analysis:');
console.log(`   originalConfig exists: ${!!originalConfig}`);
if (originalConfig) {
  console.log(`   originalConfig keys: [${Object.keys(originalConfig).join(', ')}]`);
  console.log(`   contentUrl present: ${!!originalConfig.contentUrl}`);
  console.log(`   contentUrl value: ${originalConfig.contentUrl}`);
  console.log('   Full originalConfig:');
  console.log(JSON.stringify(originalConfig, null, 2));
} else {
  console.log('   originalConfig is null/undefined!');
}

if (hasMoreBatches) {
  console.log(`🔄 Will continue to next batch from index ${nextStartIndex}`);
} else {
  console.log('🎊 ALL BATCHES COMPLETED!');
}

// Pass through exact data from Process Remote Content - no recalculation
return [{ json: {
  hasMoreBatches: hasMoreBatches,
  nextStartIndex: nextStartIndex,
  currentBatch: currentBatch,
  totalBatches: totalBatches,
  totalContentItems: totalContentItems,
  processedSoFar: nextStartIndex,
  originalConfig: originalConfig
}}];

// Take the input item from Batch Completion Check
const inputItem = $input.item.json;
const config = inputItem.originalConfig;
const nextStartIndex = inputItem.nextStartIndex;

console.log(`🔄 SETUP NEXT BATCH: Starting from index ${nextStartIndex}`);
console.log('🔍 DEBUG: originalConfig:', JSON.stringify(config, null, 2));

// Validate that we have the required originalConfig
if (!config) {
  console.error('❌ CRITICAL: originalConfig is undefined!');
  console.error('📋 Available inputItem keys:', Object.keys(inputItem));
  throw new Error('originalConfig is required but was not found in batch data. Check Process Remote Content node.');
}

if (!config.contentUrl) {
  console.error('❌ CRITICAL: contentUrl is missing from originalConfig!');
  console.error('📋 Available config keys:', Object.keys(config));
  throw new Error('contentUrl is required but was not preserved in originalConfig. Check Process Remote Content node configuration.');
}

// Create new configuration for next batch with the correct startIndex
const newConfig = {
  ...config,  // Preserve ALL original config values
  startIndex: nextStartIndex  // Only override the startIndex
};

console.log(`🚀 Sending to Process Remote Content:`);
console.log(`📤 contentUrl: ${newConfig.contentUrl}`);
console.log(`📤 startIndex: ${newConfig.startIndex}`);
console.log(`📤 maxItems: ${newConfig.maxItems}`);

return [{
  json: newConfig
}];

console.log('✅ More batches available - continuing to next batch');
console.log('🔄 About to wait 3 seconds before next batch');
return $input.all();

const current = $input.item.json;
const uploadResult = current;
const publicId = uploadResult.public_id;
const secureUrl = uploadResult.secure_url;
const bytes = uploadResult.bytes || 0;

console.log(`✅ Successfully uploaded: ${current.fileName || 'audio file'}`);
console.log(`🆔 Cloudinary public ID: ${publicId}`);
console.log(`📊 File size: ${Math.round(bytes / 1024)} KB`);
console.log('🔍 DEBUG LOG SUCCESS: Checking originalConfig preservation...');
if (current.originalConfig) {
  console.log('   originalConfig keys:', Object.keys(current.originalConfig));
  console.log('   contentUrl present:', !!current.originalConfig.contentUrl);
  console.log('   contentUrl value:', current.originalConfig.contentUrl);
} else {
  console.log('   ❌ originalConfig missing from upload result!');
}

return $input.all();

// Check what we received from Cloudinary and merge with original data
const uploadResults = $input.all();
const originalDataItems = $('Prepare for Upload').all();

console.log(`🔍 UPLOAD CHECK: Received ${uploadResults.length} items from Cloudinary`);
console.log(`🔍 ORIGINAL DATA: Have ${originalDataItems.length} original items`);

// Process each item and merge Cloudinary response with original data
const results = uploadResults.map((uploadItem, index) => {
  const cloudinaryData = uploadItem.json;
  const originalData = originalDataItems[index]?.json || {};

  console.log(`📦 Item ${index + 1}: ${originalData.fileName || 'Unknown'}`);
  console.log('  Cloudinary Error:', cloudinaryData.error);
  console.log('  Public ID:', cloudinaryData.public_id);
  console.log('  Secure URL:', cloudinaryData.secure_url);
  console.log('  Original data preserved:', !!originalData.originalConfig);

  // Check if upload was successful
  const isSuccess = !cloudinaryData.error && cloudinaryData.public_id;
  console.log(`  Success: ${isSuccess}`);

  // Merge original data with Cloudinary response
  const mergedData = {
    ...originalData,  // Preserve all original fields (originalConfig, nextStartIndex, etc.)
    ...cloudinaryData,  // Add Cloudinary response (public_id, secure_url, etc.)
    // Ensure fileName is preserved from original if missing from Cloudinary
    fileName: originalData.fileName || cloudinaryData.display_name || 'unknown.wav'
  };

  console.log('  Merged data keys:', Object.keys(mergedData));
  console.log('  originalConfig preserved:', !!mergedData.originalConfig);
  if (mergedData.originalConfig) {
    console.log('  contentUrl preserved:', !!mergedData.originalConfig.contentUrl);
  }

  return {
    json: mergedData,
    success: isSuccess
  };
});

// Separate successful and failed uploads
const successful = results.filter(r => r.success);
const failed = results.filter(r => !r.success);

console.log(`📊 UPLOAD SUMMARY:`);
console.log(`  Successful: ${successful.length}`);
console.log(`  Failed: ${failed.length}`);

if (successful.length > 0) {
  console.log('✅ Sending successful uploads with original data preserved to Log Success');
  return successful.map(r => ({ json: r.json }));
} else {
  console.log('❌ No successful uploads - sending to error handling');
  return failed.map(r => ({ json: r.json }));
}

// Pass through all input items with proper binary data preservation and filename
return $input.all().map(item => {
  // Safety check for fileName
  const fileName = item.json.fileName || 'unknown.wav';
  const fileNameWithoutExt = fileName.replace('.wav', '');

  // Check if we have valid binary data
  if (!item.binary || !item.binary.data) {
    console.error(`❌ No binary audio data found for ${fileName}`);
    console.error('This usually means ElevenLabs API returned an error instead of audio');
    console.error('Check: API key, voice ID, quota, and request format');

    return {
      json: {
        ...item.json,
        error: 'No binary audio data - ElevenLabs API may have failed',
        fileName: fileName,
        publicId: `${item.json.cloudinaryFolder}/${fileNameWithoutExt}`,
        uploadReady: false
      }
    };
  }

  // Validate binary data integrity to prevent contamination
  const audioSize = item.binary.data.length;
  const isValidAudio = audioSize > 1000; // Audio files should be larger than 1KB

  console.log(`✅ Binary audio data found for ${fileName}`);
  console.log(`📊 Audio size: ${Math.round(audioSize / 1024)} KB`);

  if (!isValidAudio) {
    console.warn(`⚠️ Audio file ${fileName} seems too small (${audioSize} bytes) - possible corruption`);
  }

  return {
    json: {
      ...item.json,
      publicId: `${item.json.cloudinaryFolder}/${fileNameWithoutExt}`,
      uploadReady: true,
      audioSize: audioSize,
      // Add execution timestamp to track freshness
      generatedAt: new Date().toISOString()
    },
    binary: {
      data: {
        ...item.binary.data,
        fileName: fileName,
        mimeType: 'audio/mpeg'
      }
    }
  };
});

// Process ALL items, not just the first one
const allItems = $input.all();

console.log(`🎙️ Processing ElevenLabs payloads for ${allItems.length} items`);

// Helper function to snap stability to required values: 0.0, 0.5, 1.0
const snapStability = (value) => {
  if (value <= 0.25) return 0.0;  // Creative
  if (value <= 0.75) return 0.5;  // Natural  
  return 1.0;                     // Robust
};

// Process each item and build ElevenLabs payload
return allItems.map((item, index) => {
  const current = item.json;

  console.log(`🎙️ Item ${index + 1}/${allItems.length}: ${current.fileName}`);
  console.log(`👤 Speaker: ${current.speaker}`);
  console.log(`📝 Text: ${current.text.substring(0, 100)}...`);
  console.log(`🎯 Voice ID: ${current.voiceId}`);
  console.log(`🚀 Model: ${current.modelId}`);
  console.log(`📊 Item ${current.itemIndex}/${current.totalItems} in batch ${current.currentBatch}/${current.totalBatches}`);

  // Build ElevenLabs API payload with required stability values
  const payload = {
    text: current.text,
    model_id: current.modelId || 'eleven_turbo_v2_5',
    voice_settings: {
      stability: snapStability(current.voiceSettings?.stability || 0.5),
      similarity_boost: Math.min(Math.max(current.voiceSettings?.similarityBoost || 0.7, 0.0), 1.0),
      style: Math.min(Math.max(current.voiceSettings?.style || 0.0, 0.0), 1.0),
      use_speaker_boost: current.voiceSettings?.useSpeakerBoost !== undefined ? current.voiceSettings.useSpeakerBoost : true
    }
  };

  console.log(`🚀 ElevenLabs payload prepared for ${current.speaker}:`);
  console.log(`   Model: ${payload.model_id}`);
  console.log(`   Voice settings: ${JSON.stringify(payload.voice_settings)}`);
  console.log(`   Stability snapped to: ${payload.voice_settings.stability} (0.0=Creative, 0.5=Natural, 1.0=Robust)`);

  return {
    json: {
      ...current,
      elevenlabsPayload: payload
    }
  };
});

const config = $input.item.json;

// Clear any potential state contamination
console.log('🧹 Clearing workflow state for fresh execution');
console.log('📤 Passing configuration to Fetch Remote JSON:');
console.log('🔍 Full config object:', JSON.stringify(config, null, 2));
console.log(`   Content URL: ${config.contentUrl}`);
console.log(`   Max Items: ${config.maxItems}`);
console.log(`   Start Index: ${config.startIndex}`);
console.log(`   Execution ID: ${new Date().getTime()}`);

// Safety check for contentUrl
if (!config.contentUrl) {
  console.error('❌ CRITICAL ERROR: contentUrl is undefined!');
  console.error('📋 Available config keys:', Object.keys(config));
  throw new Error('contentUrl is required for Fetch Remote JSON but was undefined. Check Setup Next Batch or Set Configuration nodes.');
}

// Verify URL format
if (!config.contentUrl.startsWith('http')) {
  console.error(`❌ INVALID URL FORMAT: ${config.contentUrl}`);
  throw new Error('contentUrl must be a valid HTTP/HTTPS URL');
}

console.log('✅ Configuration validation passed');
return $input.all();

console.log('🌐 Processing v3 Alpha remote content');
console.log('🔐 Using v3 Alpha model with Audio Tags support');

// DEBUG: Check all available data sources
console.log('🔍 DEBUG: Available data sources:');
console.log('  $json keys:', Object.keys($json));
console.log('  $input.all().length:', $input.all().length);
if ($input.all().length > 0) {
  console.log('  $input.first().json keys:', Object.keys($input.first().json || {}));
}

// Get the remote JSON data and configuration from input context
const remoteData = $json;

// TRY MULTIPLE CONFIG SOURCES
let config;
try {
  // Method 1: Reference previous node (original approach)
  config = $('Pass Config to Fetch').item.json;
  console.log('✅ Got config from node reference');
} catch (error) {
  console.log('❌ Node reference failed:', error.message);
  // Method 2: Use input data
  config = $input.first()?.json;
  console.log('🔄 Trying input reference instead');
}

console.log('🔍 DEBUG: Config object:');
console.log('  config exists:', !!config);
if (config) {
  console.log('  config keys:', Object.keys(config));
  console.log('  contentUrl:', config.contentUrl);
  console.log('  maxItems:', config.maxItems);
  console.log('  startIndex:', config.startIndex);
}

// Validate config
if (!config) {
  console.error('❌ CRITICAL: No config found from any source!');
  throw new Error('Configuration not found. Check data flow from Pass Config to Fetch node.');
}

if (!config.contentUrl) {
  console.error('❌ CRITICAL: contentUrl missing from config!');
  console.error('📋 Available config:', JSON.stringify(config, null, 2));
  throw new Error('contentUrl is required but missing from configuration data.');
}

const maxItems = config.maxItems || 5;
const startIndex = config.startIndex || 0;
const victorVoiceId = config.victorVoiceId;
const lennyVoiceId = config.lennyVoiceId;
const cloudinaryFolder = config.cloudinaryFolder;
const cloudName = config.cloudName;

console.log('📊 Max items per batch:', maxItems);
console.log('🎯 Starting from index:', startIndex);
console.log('✅ Successfully received v3 Alpha remote JSON');

// Extract content array
let contentItems;
if (Array.isArray(remoteData)) {
  contentItems = remoteData;
  console.log('📋 Content is direct array');
} else if (remoteData.content && Array.isArray(remoteData.content)) {
  contentItems = remoteData.content;
  console.log('📋 Content found in .content property');
} else {
  console.error('❌ Invalid remote JSON format - no content array found');
  throw new Error('Remote JSON must be array or object with content array');
}

console.log(`📚 Total content items available: ${contentItems.length}`);

// Get default settings from remote data
const defaultSettings = remoteData.defaultSettings || {};
const voiceMapping = remoteData.voiceMapping || {};

// Apply rate limiting - only process maxItems at a time
const endIndex = Math.min(startIndex + maxItems, contentItems.length);
const selectedLines = contentItems.slice(startIndex, endIndex);
const totalBatches = Math.ceil(contentItems.length / maxItems);
const currentBatch = Math.floor(startIndex / maxItems) + 1;

console.log(`📊 Processing v3 Alpha batch ${currentBatch}/${totalBatches}: lines ${startIndex + 1} to ${endIndex}`);

if (selectedLines.length === 0) {
  console.warn('⚠️ No lines selected! All processing complete.');
  return [];
}

if (endIndex < contentItems.length) {
  console.log(`📋 Remaining lines after this batch: ${contentItems.length - endIndex}`);
  console.log(`🔄 Will automatically continue to next batch`);
} else {
  console.log('✅ This is the final batch');
}

console.log('🔍 DEBUG: Creating originalConfig with:');
console.log('  contentUrl:', config.contentUrl);
console.log('  victorVoiceId:', victorVoiceId);
console.log('  maxItems:', maxItems);

// Create individual items for processing with loop control data (preserving known good pattern)
return selectedLines.map((line, index) => {
  // Safety check for fileName
  const fileName = line.fileName || 'unknown.wav';
  const fileNameWithoutExt = fileName.replace('.wav', '');

  return {
    json: {
      ...line, // Preserve all original fields including Audio Tags
      voiceId: line.voiceId || voiceMapping[line.speaker] || (line.speaker === 'Victor' ? victorVoiceId : lennyVoiceId),
      cloudinaryFolder: cloudinaryFolder,
      cloudName: cloudName,
      itemIndex: index + 1,
      totalItems: selectedLines.length,
      batchStartIndex: startIndex,
      globalIndex: startIndex + index + 1,
      totalContentItems: contentItems.length,
      currentBatch: currentBatch,
      totalBatches: totalBatches,
      publicId: `${cloudinaryFolder}/${fileNameWithoutExt}`,
      // Loop control data
      nextStartIndex: endIndex,
      hasMoreBatches: endIndex < contentItems.length,
      originalConfig: {
        contentUrl: config.contentUrl,
        victorVoiceId: victorVoiceId,
        lennyVoiceId: lennyVoiceId,
        cloudinaryFolder: cloudinaryFolder,
        cloudName: cloudName,
        maxItems: maxItems
      }
    }
  };
});
